sections.css:
.w-cards-block {
    .w-section-content {
        @include grid-row(12);
    }
}

%w-card-hover {
    @include box-shadow(1px, 1px, 5px, rgba($shadow-color,.5), "");
    transition: .3s;
    transform: translate3d(0, 0, 0);

    &:hover {
        transform: translateY(-3px);
    }
}

.w-card {
    @include grid-column(12, 0);
    margin-bottom: $global-margin;

    @include breakpoint(landscape) {
        @include grid-column(6);
    }

    @include breakpoint(medium) {
        @include grid-column(6);
        @include grid-column-end;

        &:first-child {
            padding-left: 0;
        }

        &:last-child {
            padding-right: 0;
        }
    }

    @include breakpoint(large) {
        @include grid-column(3);
        @include grid-column-end;
        margin-bottom: 0;
    }

    &:nth-child(2n+3) {
        @include breakpoint(medium) {
            clear: left;
        }

        @include breakpoint(large) {
            clear: none;
        }
    }
}

.w-card-wrapper {
    .w-section-no__box & {
        @extend %w-card-hover;
    }
}

.w-card-product__image {
    background-color: $white;
    margin: 0;
    overflow: hidden;
    position: relative;
    text-align: center;

    @include breakpoint(large) {
        height: 190px;
    }

    img {
        height: 100%;
        width: 100%;
    }
}

.w-card-product__content {
    background-color: $white;
    line-height: 1;
    padding: $global-padding / 2;

    span {
        color: $primary-color;

        &.w-oldPrice {
            @include font-size(1.7);
            color: $medium-gray;
            display: block;
            text-decoration: line-through;
        }

        &.w-currentPrice {
            display: block;
            margin: 0;
            margin-bottom: $global-margin / 2;
        }
    }

    .w-icon {
        @include before-after-background(20px, $primary-color, 1.2, ($global-margin / 5), 0, '', '\E5C8');
    }
}

.w-card-product__title {
    @include font-size(2);
    font-weight: $global-weight-bold;
    margin-bottom: 0;

    @include breakpoint(large) {
        @include font-size(1.5);
    }
}

.w-card__description {
    @include font-size(1.4);
    height: 38px;
    margin-bottom: 0;
}

.w-card__bullets {
    @include font-size(1.3);
    list-style-position: inside;
    margin-bottom: 0;

    @include breakpoint(medium) {
        @include font-size(1.4);
    }

    li {
        margin-bottom: ($global-margin / 5);

        &:last-child {
            margin-bottom: 0;
        }
    }
}

.w-card__footer {
    span {
        @include font-size(1.2);
        color: $medium-gray;
        display: block;
        margin-bottom: ($global-margin / 4);

        &:last-child {
            margin-bottom: 0;
        }
    }
}

.w-card-big {
    @include grid-column(12);
    margin-bottom: $global-margin;
    position: relative;

    @include breakpoint(large) {
        @include grid-column(6);
    }

    &.w-section-content__box {
        @include w-box(transparent, 0, 0, 0, transparent);
    }

    &:only-child {
        @include breakpoint(medium) {
            margin-bottom: 0;
        }
    }

    .w-card-product__content {
        @include grid-row(12);
        background: none;
        padding: $global-padding;
        position: absolute;
        top: 0;

        @include breakpoint(medium) {
            background-color: transparent;
        }
    }

    .w-card-product__details {
        @include breakpoint(xmedium) {
            @include grid-column(9);
        }
    }

    .w-card-product__image {
        @include breakpoint(medium) {
            left: auto;
            top: auto;
        }

        @include breakpoint(large) {
            height: auto;
        }

        img {
            @include breakpoint(large) {
                max-height: 290px;
            }
        }
    }

    .w-card-product__thumb {
        margin: 0;
        text-align: center;

        @include breakpoint(xmedium) {
            @include grid-column(3);
        }
    }

    .w-card-product__title {
        @include font-size(2);
        margin-bottom: $global-margin;

        @include breakpoint(medium) {
            @include font-size(2.75);
        }
    }

    .w-card__description {
        height: auto;
    }

    .w-card-product__pic {
        margin-bottom: ($global-margin * 2);
    }
}

.w-card-product-big {
    .w-card-product__content {
        @include breakpoint(large) {
            @include grid-row;
        }
    }

    .w-card-product__description {
        @include breakpoint(large) {
            @include grid-column(8);
            margin-bottom: 0;
        }
    }

    .w-card-product__action {
        @include breakpoint(large) {
            @include grid-column(4);
        }
    }
}

// ----------------------------------------
// Cards Layout :: Card :: Side
// ----------------------------------------
.w-card-side {
    @include grid-column(12);

    &:last-child {
        @include breakpoint(medium) {
            margin-bottom: 0;
        }
    }

    .w-card-wrapper {
        @include breakpoint(large) {
            @include grid-row;
        }
    }

    .w-card-product__content {
        padding: $global-padding;

        @include breakpoint(large) {
            @include grid-column(6);
        }
    }

    .w-card-product__image {
        @include breakpoint(large) {
            @include grid-column(6, 0);
        }
    }

    .w-card-product__title {
        @include font-size(3);
        margin-bottom: ($global-margin / 2);
    }

    .w-card__description {
        @include font-size(1.4);
    }

    &.w-card-big-image {
        .w-card-product__content {
            padding: ($global-padding * 1.5);

            @include breakpoint(large) {
                @include grid-column(4);
            }
        }

        .w-card-product__image {
            @include breakpoint(large) {
                @include grid-column(8, 0);
            }
        }

        .w-card-product__title {
            @include font-size(2);
        }

        .w-card__description {
            @include font-size(1.5);

            @include breakpoint(medium) {
                @include font-size(1.8);
            }
        }
    }
}

// ----------------------------------------
// Cards Layout :: Big Card :: Side Content
// ----------------------------------------
.w-card-big-side {
    @include grid-column(12);

    &:last-child {
        @include breakpoint(medium) {
            margin-bottom: 0;
        }
    }

    .w-card-wrapper {
        @include breakpoint(medium) {
            @include grid-row(12);
        }
    }

    .w-card-product__content {
        padding: $global-padding;

        @include breakpoint(medium) {
            @include grid-column(5);
        }

        .w-card-product__title {
            @include font-size(1.7);
        }

        .w-card__description {
            margin-bottom: $global-margin;

            @include breakpoint(medium) {
                @include font-size(1.8);
            }
        }
    }

    .w-card-product__image {
        @include breakpoint(medium) {
            @include grid-column(7, 0);
        }
    }
}

// -----------------------------------------------
// Cards Layout :: With Icon Used in Contacts Page
// -----------------------------------------------
.w-cards__icon {
    margin-bottom: $global-margin;

    @include breakpoint(medium) {
        @include grid-column(6);
    }

    &:nth-child(2n+3) {
        clear: left;
    }

    .w-cards__icon_wrapper {
        padding: $global-padding;
    }

    .w-cards__icon-box {
        @include w-box($white, 0, 0, 5px, rgba($shadow-color, .5));
    }

    header {
        margin-bottom: ($global-margin / 2);

        .material-icons {
            @include font-size(4.5);
            color: $primary-color;
        }
    }

    h3 {
        @include font-size(1.7);
        font-weight: $global-weight-bold;
    }

    h4 {
        @include font-size(1.6);
        color: $primary-color;
        font-weight: $global-weight-normal;
    }

    li {
        @include font-size(1.4);
        color: $dark-gray;
        font-weight: $global-weight-thin;
    }

    footer {
        margin-top: $global-margin;
        text-align: right;

        a {
            color: $primary-color;
        }
    }
}

.w-full-card {
    @include breakpoint(medium) {
        margin-bottom: 0;
    }

    .w-card-wrapper {
        position: relative;
    }

    .w-full-card__image {
        margin: 0;
        overflow: hidden;
        position: relative;
        text-align: center;

        img {
            height: 100%;
            width: 100%;
        }
    }

    .w-full-card__content {
        background-color: transparent;
        bottom: 0;
        left: 0;
        padding: $global-padding;
        position: absolute;
        text-align: center;
        width: 100%;
    }

    .w-full-card__title {
        @include font-size(2);
        color: $white;

        b {
            @include font-size(2.5);
            display: block;
            font-weight: $global-weight-bold;
        }
    }

    a {
        color: $white;
    }

    .w-full-card__button {
        width: 100%;
    }

    &.swiper-slide {
        margin-bottom: 0;
        padding: 0;

        .w-full-card__content {
            padding: 0;
        }

        .w-full-card__button {
            margin-bottom: $global-margin;
            width: auto;
        }
    }
}

// -----------------------
// Cards Layout :: Product
// -----------------------
.w-card-product {
    .w-card-product__description {
        margin-bottom: $global-margin;
    }

    .w-button-primary {
        margin-bottom: 0;
        width: 100%;
    }
}

.w-card-category {
    margin-bottom: $global-margin;

    @include breakpoint(large) {
        margin-bottom: ($global-margin / 3);
    }

    .w-card-product__content {
        height: auto;
        padding: ($global-padding / 2) $global-padding;
    }

    .w-card-product__subtitle {
        @include font-size(1.2);
        display: block;
        height: 16px;
    }

    .w-card-product__title {
        @include font-size(2.7);
        color: $primary-color;
        display: block;
        font-weight: $global-weight-black;
        margin-bottom: ($global-margin / 2);
        text-transform: uppercase;

        @include breakpoint(medium) {
            @include font-size(2);
        }
    }

    .w-card__description {
        @include font-size(1.3);
        height: 36px;
        margin-bottom: ($global-margin / 2);
    }

    .w-card__footer {
        span {
            @include font-size(1.2);
            color: $medium-gray;
            display: block;
            margin-bottom: ($global-margin / 4);
        }
    }
}

.w-button-primary {
  @include button(false, $primary-color, $button-background-hover, $white);
  @extend %w-button;
  box-shadow: 0 -3px $button-background-hover inset;
}

.w-button-secondary {
  @include button(false, $primary-color, $primary-color, $primary-color, hollow);
  @extend %w-button;
}

.w-button-tertiary {
  @include button(false, $medium-gray, $medium-gray, $medium-gray, hollow);
  @extend %w-button;
}

.w-button-simple {
  @include button(false, $medium-gray, transparent, transparent, hollow);
  box-shadow: none;
  @extend %w-button;
}

.w-button-facebook {
  @include button(false, $facebook-color, $facebook-color, $white);
  @extend %w-button-simple;
  border-bottom: 3px solid darken($facebook-color, 10%);
}

.w-button-twitter {
  @include button(false, $twitter-color, $twitter-color, $white);
  @extend %w-button-simple;
  border-bottom: 3px solid darken($twitter-color, 10%);
}

.w-button-google {
  @include button(false, $google-color, $google-color, $white);
  @extend %w-button-simple;
  border-bottom: 3px solid darken($google-color, 10%);
}

.w-button-icon {
  padding: .61rem $global-padding;
}

.w-button-nomargin {
  margin-bottom: 0;
}

.w-banner__container {
    position: relative;
}

.w-banner__background-image {
    img {
        height: 100%;
        width: 100%;
    }
}

.w-banner__content {
    color: $white;
    left: 0;
    position: absolute;
    text-align: center;
    top: 15%;
    width: 100%;

    @include breakpoint(medium) {
        padding-left: ($global-padding * 3);
        text-align: left;
    }
}

.w-banner__title {
    @include font-size(4);
    font-weight: $global-weight-bold;

    @include breakpoint(medium) {
        @include font-size(7);
    }

    &.w-banner__font-big {
        @include font-size(4.5);

        @include breakpoint(medium) {
            @include font-size(7.5);
        }
    }
}

.w-banner__subtitle {
    @include font-size(1.8);

    @include breakpoint(medium) {
        @include font-size(3);
    }

    &.w-banner__font-big {
        @include font-size(2.3);

        @include breakpoint(medium) {
            @include font-size(3.5);
        }
    }
}

// ---------------------
// Global Banner Wrapper
// ---------------------
.w-banner__wrapper {
    .w-section__container-center & {
        @include grid-row;
    }
}

.w-banner__wrapper__header {
    text-align: center;
}

.w-banner__wrapper__header,
.w-banner__wrapper__content {
    .w-section__container-center & {
        @include grid-column(12, 0);
    }
}

.w-banner__type-a {
    @include w-box($white, 0, 0, 5px, rgba($shadow-color, .5));
    position: relative;

    img {
        width: 100%;
    }
}

.w-banner__type-a__content {
    background-color: $white;
    bottom: ($global-margin / 2);
    left: ($global-margin / 2);
    padding: $global-padding;
    position: absolute;
    width: calc(100% - 1rem);

    @include breakpoint(large) {
        bottom: ($global-margin * 2);
        left: ($global-margin * 2);
        top: ($global-margin * 2);
        width: auto;
    }
}

.w-banner__type-a__title {
    @include font-size(2.3);
    color: $primary-color;
    font-weight: $global-weight-normal;

    @include breakpoint(medium) {
        @include font-size(2.4);
    }

    @include breakpoint(large) {
        @include font-size(2.9);
    }

    &.w-banner__font-big {
        @include font-size(2.8);

        @include breakpoint(medium) {
            @include font-size(3);
        }

        @include breakpoint(large) {
            @include font-size(3.4);
        }
    }
}

.w-banner__type-a__subtitle {
    @include font-size(1.7);
    font-weight: $global-weight-thin;

    @include breakpoint(medium) {
        @include font-size(1.8);
    }

    @include breakpoint(large) {
        @include font-size(2);
    }

    &.w-banner__font-big {
        @include font-size(2.2);

        @include breakpoint(medium) {
            @include font-size(2.2);
        }

        @include breakpoint(large) {
            @include font-size(2.5);
        }
    }
}

.w-banner__type-a__link {
    @include before-after-background(1.5rem, $primary-color, 1, $global-margin, 0, '', '\E5C8');

    @include breakpoint(large) {
        bottom: $global-margin;
        position: absolute;
        width: calc(100% - 2rem);
    }

    &:after {
        position: absolute;
        top: -2px;
    }
}

.w-banner__type-b {
    @include w-box($white, 0, 0, 5px, rgba($shadow-color, .5));
    color: $white;
    overflow: hidden;
    position: relative;
}

.w-banner__type-b__text {
    padding: ($global-padding * 2);
    position: absolute;
    z-index: 1;

    @include breakpoint(medium) {
        padding: ($global-padding * 3);
    }
}

.w-banner__type-b__title {
    @include font-size(3.5);

    @include breakpoint(large) {
        @include font-size(5);
    }

    &.w-banner__font-big {
        @include font-size(4);
    }

    @include breakpoint(large) {
        @include font-size(5.5);
    }
}

.w-banner__type-b__subtitle {
    @include font-size(1.5);

    @include breakpoint(large) {
        @include font-size(2.5);
    }

    &.w-banner__font-big {
        @include font-size(2);

        @include breakpoint(large) {
            @include font-size(3);
        }
    }
}

.w-banner__type-b__teaser-image {
    bottom: 0;
    left: 0;
    position: relative;
    right: 0;
    top: 0;

    img {
        height: 100%;
        width: 100%;

        @include breakpoint(medium) {
            height: auto;
        }
    }
}

.w-banner__type-c {
    @include w-box($white, 0, 0, 5px, rgba($shadow-color, .5));
    position: relative;

    img {
        width: 100%;
    }
}

.w-banner__type-c__content {
    color: $white;
    padding: $global-padding;
    position: absolute;
    top: 0;

    @include breakpoint(xsmall) {
        padding: ($global-padding * 3);
    }

    @include breakpoint(medium) {
        @include grid-column(10, 0);
    }

    @include breakpoint(large) {
        @include grid-column(8, 0);
    }

    &.w-align-right {
        @include breakpoint(medium) {
            @include grid-column-offset(1);
        }

        @include breakpoint(large) {
            @include grid-column-offset(4);
        }
    }

    &.full-width {
        @include breakpoint(large) {
            @include grid-column(12, 0);
        }
    }
}

.w-banner__type-c__title {
    @include font-size(3.5);

    @include breakpoint(medium) {
        @include font-size(4);
        font-weight: $global-weight-bold;
    }

    &.w-banner__font-big {
        @include font-size(3.8);

        @include breakpoint(medium) {
            @include font-size(4.5);
        }
    }
}

.w-banner__type-c__subtitle {
    @include font-size(1.5);

    @include breakpoint(xsmall) {
        @include font-size(1.8);
    }

    @include breakpoint(medium) {
        @include font-size(2.5);
    }

    &.w-banner__font-big {
        @include font-size(2.2);

        @include breakpoint(medium) {
            @include font-size(2.75);
        }
    }
}

.w-banner__type-d {
    margin-bottom: $global-margin;

    &:last-child {
        margin-bottom: 0;
    }
}

.w-banner__type-d__wrapper {
    @include w-box($white, 0, 0, 5px, rgba($shadow-color, .5));

    @include breakpoint(large) {
        @include grid-row(12, collapse , 100%, true, 10);
    }
}

.w-banner__type-d__content {
    padding: $global-padding;

    @include breakpoint(large) {
        @include grid-column(6, 0);
    }
}

.w-banner__type-d__title {
    @include font-size(3);
    font-weight: $global-weight-normal;
    margin-bottom: ($global-margin / 2);
    &.w-banner__font-big {
        @include font-size(3.5);
    }
}

.w-banner__type-d__subtitle {
    @include font-size(1.4);

    @include breakpoint(medium) {
        @include font-size(1.5);
    }


    &.w-banner__font-big {
        @include font-size(1.9);

        @include breakpoint(medium) {
            @include font-size(1.8);
        }
    }
}

.w-banner__type-d__description {
    @include breakpoint(medium) {
        @include font-size(1.4);
    }
}

.w-banner__type-d__image {
    background-color: $white;
    margin: 0;
    overflow: hidden;
    position: relative;
    text-align: center;

    @include breakpoint(large) {
        @include grid-column(6, 0);
    }

    img {
        height: 100%;
        width: 100%;
    }
}

.w-banner__type-d__item {
    @include breakpoint(large) {
        width: 33%;
    }
}

.w-banner__type-d__sideimage {
    .w-banner__type-d__wrapper {
        @include breakpoint(large) {
            display: flex;
            justify-content: space-around;
        }
    }

    .w-banner__type-d__image {
        flex-basis: 66%;
        flex-grow: 2;

        @include breakpoint(large) {
            @include grid-column(8, 0);
        }
    }

    .w-banner__type-d__content {
        padding: ($global-padding * 1.5);
        flex-basis: 33%;
        flex-grow: 1;


        @include breakpoint(large) {
            @include grid-column(4, 0);
        }
    }

    .w-banner__type-d__title {
        @include font-size(2);
        &.w-banner__font-big {
            @include font-size(2.5);
        }
    }

    .w-banner__type-d__subtitle {
        @include font-size(1.5);
        &.w-banner__font-big {
            @include font-size(2);
        }

        &:last-child {
            margin-bottom: 0;
        }
    }
}

.w-banner-slider__type-a {
    .w-section__container-center & {
        @include grid-row;
    }

    .w-banner-slider__type-a__link {
        color: $white;
        display: block;
    }

    .section-container & {
        padding: 0;
    }

    .w-section__container-center & {
        @include breakpoint(medium) {
            margin-bottom: $global-margin;
        }

        .w-banner-slider__type-a__wrapper {
            @include breakpoint(medium) {
                @include grid-column(12);
            }
        }
    }

    .w-banner-slider__type-a__wrapper {
        .section-container &,
        .w-section__container-center & {
            @include breakpoint(medium) {
                padding: 0;
            }
        }
    }
}

.w-banner-slider__type-a__container {
    @include breakpoint(medium) {
        @include grid-row(13) {
            .w-banner-slider__type-a__product__image {
                @include grid-column(7);
            }

            .w-banner-slider__type-a__product__content {
                height: 330px;
                padding-bottom: 0;
                padding-top: 0;
                @include grid-column(5);
            }
        }
    }

    &.center-image {
        .w-banner-slider__type-a__product__image {
            @include breakpoint(medium) {
                @include grid-column(5);
                @include grid-column-offset(4);
                text-align: right;
            }
        }

        .w-banner-slider__type-a__product__content {
            @include breakpoint(medium) {
                @include grid-column(3);
            }
        }
    }

    &.left-image {
        .w-banner-slider__type-a__product__image {
            @include breakpoint(medium) {
                @include grid-column-offset(0);
                text-align: right;
            }
        }

        .w-banner-slider__type-a__product__content {
            @include breakpoint(medium) {
                @include grid-column-end;
            }
        }
    }

    &.right-image {
        .w-banner-slider__type-a__product__image {
            @include breakpoint(medium) {
                @include grid-col-pos(7);
                @include grid-column-offset(0);
                text-align: left;
            }
        }

        .w-banner-slider__type-a__product__content {
            @include breakpoint(medium) {
                @include grid-column(5);
                @include grid-col-pos(-6);
                @include grid-column-offset(0);
                text-align: right;
            }
        }
    }

    .w-banner-slider__content__wrapper {
        @include breakpoint(medium) {
            @include vertical-align(relative);
        }
    }


    .w-banner-slider__type-a__product__image {
        @include breakpoint(xxlarge) {
            @include grid-column(6);
        }
    }

    .w-banner-slider__type-a__product__content {
        @include breakpoint(xxlarge) {
            height: 425px;
        }
    }
}

.w-banner-slider__type-a__product {
    min-height: 600px;
    position: relative;

    @include breakpoint(medium) {
        min-height: 330px;
    }

    @include breakpoint(large) {
        min-height: 375px;
    }

    @include breakpoint(xxlarge) {
        min-height: 425px;
    }

    .w-section__container-center & {
        @include breakpoint(large) {
            min-height: 360px;
        }
    }
}

// ----------------------------------------
// Banner Slider Type A :: Background Image
// ----------------------------------------
.w-banner-slider__type-a__product__background-image {
    height: 100%;
    left: 0;
    position: absolute;
    text-align: center;
    top: 0;
    width: 100%;
    z-index: -1;

    @include breakpoint(medium) {
        z-index: 0;
    }

    img {
        height: 600px;

        @include breakpoint(medium) {
            height: 330px;
            width: 100%;
        }

        @include breakpoint(large) {
            height: 375px;
        }

        @include breakpoint(xxlarge) {
            height: 425px;
        }
    }
}

.w-banner-slider__type-a__product__image {
    margin: 0;
    min-height: 100px;
    text-align: center;

    @include breakpoint(medium) {
        height: 330px;
    }

    @include breakpoint(large) {
        height: 375px;
    }

    @include breakpoint(xxlarge) {
        height: 425px;
    }

    img {
        @include breakpoint(medium) {
            max-width: 330px;
        }

        @include breakpoint(large) {
            max-width: 375px;
        }

        @include breakpoint(xxlarge) {
            max-width: 425px;
        }
    }

    .w-section__container-center & {
        @include breakpoint(large) {
            height: 360px;
        }
    }
}

.w-banner-slider__type-a__title {
    @include font-size(2.6);
    font-weight: $global-weight-bold;

    @include breakpoint(large) {
        @include font-size(3);
    }
}

.w-banner-slider__type-a__description {
    @include font-size(1.4);
    line-height: 1.2;

    @include breakpoint(xmedium) {
        @include font-size(1.6);
    }

    @include breakpoint(large) {
        @include font-size(1.8);
    }
}

.w-banner-slider__type-a__button-next,
.w-banner-slider__type-a__button-prev {
    @include show-for(medium);
}

.w-banner-slider__type-a__button-prev {
    left: $global-margin;
}

.w-banner-slider__type-a__button-next {
    right: $global-margin;
}

.w-banner-slider__type-a__product__content {
    padding: ($global-padding * 1.5) $global-padding;

    @include breakpoint(medium) {
        padding-top: ($global-padding * 2);
    }

    @include breakpoint(large) {
        height: 375px;
        padding-top: 0;
    }

    .w-product__price__old {
        @include font-size(1.8);

        @include breakpoint(large) {
            @include font-size(2.2);
        }
    }

    .w-product__price__current {
        @include font-size(3.2);

        @include breakpoint(large) {
            @include font-size(4.5);
        }
    }

    .w-section__container-center & {
        @include breakpoint(large) {
            height: 360px;
        }
    }
}

.w-banner-slider__type-a__footer {
    p {
        @include font-size(1.2);
    }

    span {
        @include font-size(1.2);
        display: block;
        line-height: 1.2;
        font-weight: $global-weight-thin;
    }

    .w-banner-slider__type-a__button {
        margin-bottom: 0;
        margin-top: ($global-margin * 1.2);
        padding-left: 0;
        padding-right: 0;
        width: 100%;

        @include breakpoint(medium) {
            width: 60%;
        }
    }
}

// ---------------------------------
// Banners - Inside a section-column
// ---------------------------------
.section-column {
    .w-banner-slider__type-a {
        margin-bottom: 0;
    }

    .w-banner-slider__type-a__product {
        min-height: 200px;

        @include breakpoint(landscape) {
            height: 320px;
        }
    }

    .w-banner-slider__type-a__product__content,
    .w-banner-slider__type-a__product__image {
        @include breakpoint(medium) {
            height: 320px;
        }
    }

    .w-banner-slider__type-a__product, 
    .w-banner-slider__type-a__product__image img {
        min-height: 320px;

        @include breakpoint(medium) {
            min-height: auto;
            max-height: 320px;
        }
    }

    .w-banner-slider__type-a__container .w-banner-slider__type-a__product__content {
        padding-bottom: ($global-padding * 2);

        img {
            max-height: 320px;
        }

        @include breakpoint(landscape) {
            height: 320px;
        }

        @include breakpoint(medium) {
            height: 320px;
            padding-bottom: 0;
        }
    }

    .w-banner-slider__type-a__container {
        // center image
        &.center-image {
            .w-banner-slider__type-a__product__image {
                @include breakpoint(medium) {
                    @include grid-column(5);
                    @include grid-column-offset(2);
                    text-align: right;
                }
            }

            .w-banner-slider__type-a__product__content {
                @include breakpoint(medium) {
                    @include grid-column(5);
                }
            }
        }

        &.right-image {
            .w-banner-slider__type-a__product__image {
                @include breakpoint(medium) {
                    @include grid-column(6);
                    @include grid-col-pos(6);
                    @include grid-column-offset(0);
                }
            }

            .w-banner-slider__type-a__product__content {
                @include breakpoint(medium) {
                    @include grid-column(5);
                    @include grid-col-pos(-7);
                    @include grid-column-offset(0);
                }
            }
        }
    }
}

section.css:
.w-cards-block {
    .w-section-content {
        @include grid-row(12);
    }
}

%w-card-hover {
    @include box-shadow(1px, 1px, 5px, rgba($shadow-color,.5), "");
    transition: .3s;
    transform: translate3d(0, 0, 0);

    &:hover {
        transform: translateY(-3px);
    }
}

.w-card {
    @include grid-column(12, 0);
    margin-bottom: $global-margin;

    @include breakpoint(landscape) {
        @include grid-column(6);
    }

    @include breakpoint(medium) {
        @include grid-column(6);
        @include grid-column-end;

        &:first-child {
            padding-left: 0;
        }

        &:last-child {
            padding-right: 0;
        }
    }

    @include breakpoint(large) {
        @include grid-column(3);
        @include grid-column-end;
        margin-bottom: 0;
    }

    &:nth-child(2n+3) {
        @include breakpoint(medium) {
            clear: left;
        }

        @include breakpoint(large) {
            clear: none;
        }
    }
}

.w-card-wrapper {
    .w-section-no__box & {
        @extend %w-card-hover;
    }
}

.w-card-product__image {
    background-color: $white;
    margin: 0;
    overflow: hidden;
    position: relative;
    text-align: center;

    @include breakpoint(large) {
        height: 190px;
    }

    img {
        height: 100%;
        width: 100%;
    }
}

.w-card-product__content {
    background-color: $white;
    line-height: 1;
    padding: $global-padding / 2;

    span {
        color: $primary-color;

        &.w-oldPrice {
            @include font-size(1.7);
            color: $medium-gray;
            display: block;
            text-decoration: line-through;
        }

        &.w-currentPrice {
            display: block;
            margin: 0;
            margin-bottom: $global-margin / 2;
        }
    }

    .w-icon {
        @include before-after-background(20px, $primary-color, 1.2, ($global-margin / 5), 0, '', '\E5C8');
    }
}

.w-card-product__title {
    @include font-size(2);
    font-weight: $global-weight-bold;
    margin-bottom: 0;

    @include breakpoint(large) {
        @include font-size(1.5);
    }
}

.w-card__description {
    @include font-size(1.4);
    height: 38px;
    margin-bottom: 0;
}

.w-card__bullets {
    @include font-size(1.3);
    list-style-position: inside;
    margin-bottom: 0;

    @include breakpoint(medium) {
        @include font-size(1.4);
    }

    li {
        margin-bottom: ($global-margin / 5);

        &:last-child {
            margin-bottom: 0;
        }
    }
}

.w-card__footer {
    span {
        @include font-size(1.2);
        color: $medium-gray;
        display: block;
        margin-bottom: ($global-margin / 4);

        &:last-child {
            margin-bottom: 0;
        }
    }
}

.w-card-big {
    @include grid-column(12);
    margin-bottom: $global-margin;
    position: relative;

    @include breakpoint(large) {
        @include grid-column(6);
    }

    &.w-section-content__box {
        @include w-box(transparent, 0, 0, 0, transparent);
    }

    &:only-child {
        @include breakpoint(medium) {
            margin-bottom: 0;
        }
    }

    .w-card-product__content {
        @include grid-row(12);
        background: none;
        padding: $global-padding;
        position: absolute;
        top: 0;

        @include breakpoint(medium) {
            background-color: transparent;
        }
    }

    .w-card-product__details {
        @include breakpoint(xmedium) {
            @include grid-column(9);
        }
    }

    .w-card-product__image {
        @include breakpoint(medium) {
            left: auto;
            top: auto;
        }

        @include breakpoint(large) {
            height: auto;
        }

        img {
            @include breakpoint(large) {
                max-height: 290px;
            }
        }
    }

    .w-card-product__thumb {
        margin: 0;
        text-align: center;

        @include breakpoint(xmedium) {
            @include grid-column(3);
        }
    }

    .w-card-product__title {
        @include font-size(2);
        margin-bottom: $global-margin;

        @include breakpoint(medium) {
            @include font-size(2.75);
        }
    }

    .w-card__description {
        height: auto;
    }

    .w-card-product__pic {
        margin-bottom: ($global-margin * 2);
    }
}

.w-card-product-big {
    .w-card-product__content {
        @include breakpoint(large) {
            @include grid-row;
        }
    }

    .w-card-product__description {
        @include breakpoint(large) {
            @include grid-column(8);
            margin-bottom: 0;
        }
    }

    .w-card-product__action {
        @include breakpoint(large) {
            @include grid-column(4);
        }
    }
}

// ----------------------------------------
// Cards Layout :: Card :: Side
// ----------------------------------------
.w-card-side {
    @include grid-column(12);

    &:last-child {
        @include breakpoint(medium) {
            margin-bottom: 0;
        }
    }

    .w-card-wrapper {
        @include breakpoint(large) {
            @include grid-row;
        }
    }

    .w-card-product__content {
        padding: $global-padding;

        @include breakpoint(large) {
            @include grid-column(6);
        }
    }

    .w-card-product__image {
        @include breakpoint(large) {
            @include grid-column(6, 0);
        }
    }

    .w-card-product__title {
        @include font-size(3);
        margin-bottom: ($global-margin / 2);
    }

    .w-card__description {
        @include font-size(1.4);
    }

    &.w-card-big-image {
        .w-card-product__content {
            padding: ($global-padding * 1.5);

            @include breakpoint(large) {
                @include grid-column(4);
            }
        }

        .w-card-product__image {
            @include breakpoint(large) {
                @include grid-column(8, 0);
            }
        }

        .w-card-product__title {
            @include font-size(2);
        }

        .w-card__description {
            @include font-size(1.5);

            @include breakpoint(medium) {
                @include font-size(1.8);
            }
        }
    }
}

// ----------------------------------------
// Cards Layout :: Big Card :: Side Content
// ----------------------------------------
.w-card-big-side {
    @include grid-column(12);

    &:last-child {
        @include breakpoint(medium) {
            margin-bottom: 0;
        }
    }

    .w-card-wrapper {
        @include breakpoint(medium) {
            @include grid-row(12);
        }
    }

    .w-card-product__content {
        padding: $global-padding;

        @include breakpoint(medium) {
            @include grid-column(5);
        }

        .w-card-product__title {
            @include font-size(1.7);
        }

        .w-card__description {
            margin-bottom: $global-margin;

            @include breakpoint(medium) {
                @include font-size(1.8);
            }
        }
    }

    .w-card-product__image {
        @include breakpoint(medium) {
            @include grid-column(7, 0);
        }
    }
}

// -----------------------------------------------
// Cards Layout :: With Icon Used in Contacts Page
// -----------------------------------------------
.w-cards__icon {
    margin-bottom: $global-margin;

    @include breakpoint(medium) {
        @include grid-column(6);
    }

    &:nth-child(2n+3) {
        clear: left;
    }

    .w-cards__icon_wrapper {
        padding: $global-padding;
    }

    .w-cards__icon-box {
        @include w-box($white, 0, 0, 5px, rgba($shadow-color, .5));
    }

    header {
        margin-bottom: ($global-margin / 2);

        .material-icons {
            @include font-size(4.5);
            color: $primary-color;
        }
    }

    h3 {
        @include font-size(1.7);
        font-weight: $global-weight-bold;
    }

    h4 {
        @include font-size(1.6);
        color: $primary-color;
        font-weight: $global-weight-normal;
    }

    li {
        @include font-size(1.4);
        color: $dark-gray;
        font-weight: $global-weight-thin;
    }

    footer {
        margin-top: $global-margin;
        text-align: right;

        a {
            color: $primary-color;
        }
    }
}

.w-full-card {
    @include breakpoint(medium) {
        margin-bottom: 0;
    }

    .w-card-wrapper {
        position: relative;
    }

    .w-full-card__image {
        margin: 0;
        overflow: hidden;
        position: relative;
        text-align: center;

        img {
            height: 100%;
            width: 100%;
        }
    }

    .w-full-card__content {
        background-color: transparent;
        bottom: 0;
        left: 0;
        padding: $global-padding;
        position: absolute;
        text-align: center;
        width: 100%;
    }

    .w-full-card__title {
        @include font-size(2);
        color: $white;

        b {
            @include font-size(2.5);
            display: block;
            font-weight: $global-weight-bold;
        }
    }

    a {
        color: $white;
    }

    .w-full-card__button {
        width: 100%;
    }

    &.swiper-slide {
        margin-bottom: 0;
        padding: 0;

        .w-full-card__content {
            padding: 0;
        }

        .w-full-card__button {
            margin-bottom: $global-margin;
            width: auto;
        }
    }
}

// -----------------------
// Cards Layout :: Product
// -----------------------
.w-card-product {
    .w-card-product__description {
        margin-bottom: $global-margin;
    }

    .w-button-primary {
        margin-bottom: 0;
        width: 100%;
    }
}

.w-card-category {
    margin-bottom: $global-margin;

    @include breakpoint(large) {
        margin-bottom: ($global-margin / 3);
    }

    .w-card-product__content {
        height: auto;
        padding: ($global-padding / 2) $global-padding;
    }

    .w-card-product__subtitle {
        @include font-size(1.2);
        display: block;
        height: 16px;
    }

    .w-card-product__title {
        @include font-size(2.7);
        color: $primary-color;
        display: block;
        font-weight: $global-weight-black;
        margin-bottom: ($global-margin / 2);
        text-transform: uppercase;

        @include breakpoint(medium) {
            @include font-size(2);
        }
    }

    .w-card__description {
        @include font-size(1.3);
        height: 36px;
        margin-bottom: ($global-margin / 2);
    }

    .w-card__footer {
        span {
            @include font-size(1.2);
            color: $medium-gray;
            display: block;
            margin-bottom: ($global-margin / 4);
        }
    }
}

.w-button-primary {
  @include button(false, $primary-color, $button-background-hover, $white);
  @extend %w-button;
  box-shadow: 0 -3px $button-background-hover inset;
}

.w-button-secondary {
  @include button(false, $primary-color, $primary-color, $primary-color, hollow);
  @extend %w-button;
}

.w-button-tertiary {
  @include button(false, $medium-gray, $medium-gray, $medium-gray, hollow);
  @extend %w-button;
}

.w-button-simple {
  @include button(false, $medium-gray, transparent, transparent, hollow);
  box-shadow: none;
  @extend %w-button;
}

.w-button-facebook {
  @include button(false, $facebook-color, $facebook-color, $white);
  @extend %w-button-simple;
  border-bottom: 3px solid darken($facebook-color, 10%);
}

.w-button-twitter {
  @include button(false, $twitter-color, $twitter-color, $white);
  @extend %w-button-simple;
  border-bottom: 3px solid darken($twitter-color, 10%);
}

.w-button-google {
  @include button(false, $google-color, $google-color, $white);
  @extend %w-button-simple;
  border-bottom: 3px solid darken($google-color, 10%);
}

.w-button-icon {
  padding: .61rem $global-padding;
}

.w-button-nomargin {
  margin-bottom: 0;
}

.w-banner__container {
    position: relative;
}

.w-banner__background-image {
    img {
        height: 100%;
        width: 100%;
    }
}

.w-banner__content {
    color: $white;
    left: 0;
    position: absolute;
    text-align: center;
    top: 15%;
    width: 100%;

    @include breakpoint(medium) {
        padding-left: ($global-padding * 3);
        text-align: left;
    }
}

.w-banner__title {
    @include font-size(4);
    font-weight: $global-weight-bold;

    @include breakpoint(medium) {
        @include font-size(7);
    }

    &.w-banner__font-big {
        @include font-size(4.5);

        @include breakpoint(medium) {
            @include font-size(7.5);
        }
    }
}

.w-banner__subtitle {
    @include font-size(1.8);

    @include breakpoint(medium) {
        @include font-size(3);
    }

    &.w-banner__font-big {
        @include font-size(2.3);

        @include breakpoint(medium) {
            @include font-size(3.5);
        }
    }
}

// ---------------------
// Global Banner Wrapper
// ---------------------
.w-banner__wrapper {
    .w-section__container-center & {
        @include grid-row;
    }
}

.w-banner__wrapper__header {
    text-align: center;
}

.w-banner__wrapper__header,
.w-banner__wrapper__content {
    .w-section__container-center & {
        @include grid-column(12, 0);
    }
}

.w-banner__type-a {
    @include w-box($white, 0, 0, 5px, rgba($shadow-color, .5));
    position: relative;

    img {
        width: 100%;
    }
}

.w-banner__type-a__content {
    background-color: $white;
    bottom: ($global-margin / 2);
    left: ($global-margin / 2);
    padding: $global-padding;
    position: absolute;
    width: calc(100% - 1rem);

    @include breakpoint(large) {
        bottom: ($global-margin * 2);
        left: ($global-margin * 2);
        top: ($global-margin * 2);
        width: auto;
    }
}

.w-banner__type-a__title {
    @include font-size(2.3);
    color: $primary-color;
    font-weight: $global-weight-normal;

    @include breakpoint(medium) {
        @include font-size(2.4);
    }

    @include breakpoint(large) {
        @include font-size(2.9);
    }

    &.w-banner__font-big {
        @include font-size(2.8);

        @include breakpoint(medium) {
            @include font-size(3);
        }

        @include breakpoint(large) {
            @include font-size(3.4);
        }
    }
}

.w-banner__type-a__subtitle {
    @include font-size(1.7);
    font-weight: $global-weight-thin;

    @include breakpoint(medium) {
        @include font-size(1.8);
    }

    @include breakpoint(large) {
        @include font-size(2);
    }

    &.w-banner__font-big {
        @include font-size(2.2);

        @include breakpoint(medium) {
            @include font-size(2.2);
        }

        @include breakpoint(large) {
            @include font-size(2.5);
        }
    }
}

.w-banner__type-a__link {
    @include before-after-background(1.5rem, $primary-color, 1, $global-margin, 0, '', '\E5C8');

    @include breakpoint(large) {
        bottom: $global-margin;
        position: absolute;
        width: calc(100% - 2rem);
    }

    &:after {
        position: absolute;
        top: -2px;
    }
}

.w-banner__type-b {
    @include w-box($white, 0, 0, 5px, rgba($shadow-color, .5));
    color: $white;
    overflow: hidden;
    position: relative;
}

.w-banner__type-b__text {
    padding: ($global-padding * 2);
    position: absolute;
    z-index: 1;

    @include breakpoint(medium) {
        padding: ($global-padding * 3);
    }
}

.w-banner__type-b__title {
    @include font-size(3.5);

    @include breakpoint(large) {
        @include font-size(5);
    }

    &.w-banner__font-big {
        @include font-size(4);
    }

    @include breakpoint(large) {
        @include font-size(5.5);
    }
}

.w-banner__type-b__subtitle {
    @include font-size(1.5);

    @include breakpoint(large) {
        @include font-size(2.5);
    }

    &.w-banner__font-big {
        @include font-size(2);

        @include breakpoint(large) {
            @include font-size(3);
        }
    }
}

.w-banner__type-b__teaser-image {
    bottom: 0;
    left: 0;
    position: relative;
    right: 0;
    top: 0;

    img {
        height: 100%;
        width: 100%;

        @include breakpoint(medium) {
            height: auto;
        }
    }
}

.w-banner__type-c {
    @include w-box($white, 0, 0, 5px, rgba($shadow-color, .5));
    position: relative;

    img {
        width: 100%;
    }
}

.w-banner__type-c__content {
    color: $white;
    padding: $global-padding;
    position: absolute;
    top: 0;

    @include breakpoint(xsmall) {
        padding: ($global-padding * 3);
    }

    @include breakpoint(medium) {
        @include grid-column(10, 0);
    }

    @include breakpoint(large) {
        @include grid-column(8, 0);
    }

    &.w-align-right {
        @include breakpoint(medium) {
            @include grid-column-offset(1);
        }

        @include breakpoint(large) {
            @include grid-column-offset(4);
        }
    }

    &.full-width {
        @include breakpoint(large) {
            @include grid-column(12, 0);
        }
    }
}

.w-banner__type-c__title {
    @include font-size(3.5);

    @include breakpoint(medium) {
        @include font-size(4);
        font-weight: $global-weight-bold;
    }

    &.w-banner__font-big {
        @include font-size(3.8);

        @include breakpoint(medium) {
            @include font-size(4.5);
        }
    }
}

.w-banner__type-c__subtitle {
    @include font-size(1.5);

    @include breakpoint(xsmall) {
        @include font-size(1.8);
    }

    @include breakpoint(medium) {
        @include font-size(2.5);
    }

    &.w-banner__font-big {
        @include font-size(2.2);

        @include breakpoint(medium) {
            @include font-size(2.75);
        }
    }
}

.w-banner__type-d {
    margin-bottom: $global-margin;

    &:last-child {
        margin-bottom: 0;
    }
}

.w-banner__type-d__wrapper {
    @include w-box($white, 0, 0, 5px, rgba($shadow-color, .5));

    @include breakpoint(large) {
        @include grid-row(12, collapse , 100%, true, 10);
    }
}

.w-banner__type-d__content {
    padding: $global-padding;

    @include breakpoint(large) {
        @include grid-column(6, 0);
    }
}

.w-banner__type-d__title {
    @include font-size(3);
    font-weight: $global-weight-normal;
    margin-bottom: ($global-margin / 2);
    &.w-banner__font-big {
        @include font-size(3.5);
    }
}

.w-banner__type-d__subtitle {
    @include font-size(1.4);

    @include breakpoint(medium) {
        @include font-size(1.5);
    }


    &.w-banner__font-big {
        @include font-size(1.9);

        @include breakpoint(medium) {
            @include font-size(1.8);
        }
    }
}

.w-banner__type-d__description {
    @include breakpoint(medium) {
        @include font-size(1.4);
    }
}

.w-banner__type-d__image {
    background-color: $white;
    margin: 0;
    overflow: hidden;
    position: relative;
    text-align: center;

    @include breakpoint(large) {
        @include grid-column(6, 0);
    }

    img {
        height: 100%;
        width: 100%;
    }
}

.w-banner__type-d__item {
    @include breakpoint(large) {
        width: 33%;
    }
}

.w-banner__type-d__sideimage {
    .w-banner__type-d__wrapper {
        @include breakpoint(large) {
            display: flex;
            justify-content: space-around;
        }
    }

    .w-banner__type-d__image {
        flex-basis: 66%;
        flex-grow: 2;

        @include breakpoint(large) {
            @include grid-column(8, 0);
        }
    }

    .w-banner__type-d__content {
        padding: ($global-padding * 1.5);
        flex-basis: 33%;
        flex-grow: 1;


        @include breakpoint(large) {
            @include grid-column(4, 0);
        }
    }

    .w-banner__type-d__title {
        @include font-size(2);
        &.w-banner__font-big {
            @include font-size(2.5);
        }
    }

    .w-banner__type-d__subtitle {
        @include font-size(1.5);
        &.w-banner__font-big {
            @include font-size(2);
        }

        &:last-child {
            margin-bottom: 0;
        }
    }
}

.w-banner-slider__type-a {
    .w-section__container-center & {
        @include grid-row;
    }

    .w-banner-slider__type-a__link {
        color: $white;
        display: block;
    }

    .section-container & {
        padding: 0;
    }

    .w-section__container-center & {
        @include breakpoint(medium) {
            margin-bottom: $global-margin;
        }

        .w-banner-slider__type-a__wrapper {
            @include breakpoint(medium) {
                @include grid-column(12);
            }
        }
    }

    .w-banner-slider__type-a__wrapper {
        .section-container &,
        .w-section__container-center & {
            @include breakpoint(medium) {
                padding: 0;
            }
        }
    }
}

.w-banner-slider__type-a__container {
    @include breakpoint(medium) {
        @include grid-row(13) {
            .w-banner-slider__type-a__product__image {
                @include grid-column(7);
            }

            .w-banner-slider__type-a__product__content {
                height: 330px;
                padding-bottom: 0;
                padding-top: 0;
                @include grid-column(5);
            }
        }
    }

    &.center-image {
        .w-banner-slider__type-a__product__image {
            @include breakpoint(medium) {
                @include grid-column(5);
                @include grid-column-offset(4);
                text-align: right;
            }
        }

        .w-banner-slider__type-a__product__content {
            @include breakpoint(medium) {
                @include grid-column(3);
            }
        }
    }

    &.left-image {
        .w-banner-slider__type-a__product__image {
            @include breakpoint(medium) {
                @include grid-column-offset(0);
                text-align: right;
            }
        }

        .w-banner-slider__type-a__product__content {
            @include breakpoint(medium) {
                @include grid-column-end;
            }
        }
    }

    &.right-image {
        .w-banner-slider__type-a__product__image {
            @include breakpoint(medium) {
                @include grid-col-pos(7);
                @include grid-column-offset(0);
                text-align: left;
            }
        }

        .w-banner-slider__type-a__product__content {
            @include breakpoint(medium) {
                @include grid-column(5);
                @include grid-col-pos(-6);
                @include grid-column-offset(0);
                text-align: right;
            }
        }
    }

    .w-banner-slider__content__wrapper {
        @include breakpoint(medium) {
            @include vertical-align(relative);
        }
    }


    .w-banner-slider__type-a__product__image {
        @include breakpoint(xxlarge) {
            @include grid-column(6);
        }
    }

    .w-banner-slider__type-a__product__content {
        @include breakpoint(xxlarge) {
            height: 425px;
        }
    }
}

.w-banner-slider__type-a__product {
    min-height: 600px;
    position: relative;

    @include breakpoint(medium) {
        min-height: 330px;
    }

    @include breakpoint(large) {
        min-height: 375px;
    }

    @include breakpoint(xxlarge) {
        min-height: 425px;
    }

    .w-section__container-center & {
        @include breakpoint(large) {
            min-height: 360px;
        }
    }
}

// ----------------------------------------
// Banner Slider Type A :: Background Image
// ----------------------------------------
.w-banner-slider__type-a__product__background-image {
    height: 100%;
    left: 0;
    position: absolute;
    text-align: center;
    top: 0;
    width: 100%;
    z-index: -1;

    @include breakpoint(medium) {
        z-index: 0;
    }

    img {
        height: 600px;

        @include breakpoint(medium) {
            height: 330px;
            width: 100%;
        }

        @include breakpoint(large) {
            height: 375px;
        }

        @include breakpoint(xxlarge) {
            height: 425px;
        }
    }
}

.w-banner-slider__type-a__product__image {
    margin: 0;
    min-height: 100px;
    text-align: center;

    @include breakpoint(medium) {
        height: 330px;
    }

    @include breakpoint(large) {
        height: 375px;
    }

    @include breakpoint(xxlarge) {
        height: 425px;
    }

    img {
        @include breakpoint(medium) {
            max-width: 330px;
        }

        @include breakpoint(large) {
            max-width: 375px;
        }

        @include breakpoint(xxlarge) {
            max-width: 425px;
        }
    }

    .w-section__container-center & {
        @include breakpoint(large) {
            height: 360px;
        }
    }
}

.w-banner-slider__type-a__title {
    @include font-size(2.6);
    font-weight: $global-weight-bold;

    @include breakpoint(large) {
        @include font-size(3);
    }
}

.w-banner-slider__type-a__description {
    @include font-size(1.4);
    line-height: 1.2;

    @include breakpoint(xmedium) {
        @include font-size(1.6);
    }

    @include breakpoint(large) {
        @include font-size(1.8);
    }
}

.w-banner-slider__type-a__button-next,
.w-banner-slider__type-a__button-prev {
    @include show-for(medium);
}

.w-banner-slider__type-a__button-prev {
    left: $global-margin;
}

.w-banner-slider__type-a__button-next {
    right: $global-margin;
}

.w-banner-slider__type-a__product__content {
    padding: ($global-padding * 1.5) $global-padding;

    @include breakpoint(medium) {
        padding-top: ($global-padding * 2);
    }

    @include breakpoint(large) {
        height: 375px;
        padding-top: 0;
    }

    .w-product__price__old {
        @include font-size(1.8);

        @include breakpoint(large) {
            @include font-size(2.2);
        }
    }

    .w-product__price__current {
        @include font-size(3.2);

        @include breakpoint(large) {
            @include font-size(4.5);
        }
    }

    .w-section__container-center & {
        @include breakpoint(large) {
            height: 360px;
        }
    }
}

.w-banner-slider__type-a__footer {
    p {
        @include font-size(1.2);
    }

    span {
        @include font-size(1.2);
        display: block;
        line-height: 1.2;
        font-weight: $global-weight-thin;
    }

    .w-banner-slider__type-a__button {
        margin-bottom: 0;
        margin-top: ($global-margin * 1.2);
        padding-left: 0;
        padding-right: 0;
        width: 100%;

        @include breakpoint(medium) {
            width: 60%;
        }
    }
}

// ---------------------------------
// Banners - Inside a section-column
// ---------------------------------
.section-column {
    .w-banner-slider__type-a {
        margin-bottom: 0;
    }

    .w-banner-slider__type-a__product {
        min-height: 200px;

        @include breakpoint(landscape) {
            height: 320px;
        }
    }

    .w-banner-slider__type-a__product__content,
    .w-banner-slider__type-a__product__image {
        @include breakpoint(medium) {
            height: 320px;
        }
    }

    .w-banner-slider__type-a__product, 
    .w-banner-slider__type-a__product__image img {
        min-height: 320px;

        @include breakpoint(medium) {
            min-height: auto;
            max-height: 320px;
        }
    }

    .w-banner-slider__type-a__container .w-banner-slider__type-a__product__content {
        padding-bottom: ($global-padding * 2);

        img {
            max-height: 320px;
        }

        @include breakpoint(landscape) {
            height: 320px;
        }

        @include breakpoint(medium) {
            height: 320px;
            padding-bottom: 0;
        }
    }

    .w-banner-slider__type-a__container {
        // center image
        &.center-image {
            .w-banner-slider__type-a__product__image {
                @include breakpoint(medium) {
                    @include grid-column(5);
                    @include grid-column-offset(2);
                    text-align: right;
                }
            }

            .w-banner-slider__type-a__product__content {
                @include breakpoint(medium) {
                    @include grid-column(5);
                }
            }
        }

        &.right-image {
            .w-banner-slider__type-a__product__image {
                @include breakpoint(medium) {
                    @include grid-column(6);
                    @include grid-col-pos(6);
                    @include grid-column-offset(0);
                }
            }

            .w-banner-slider__type-a__product__content {
                @include breakpoint(medium) {
                    @include grid-column(5);
                    @include grid-col-pos(-7);
                    @include grid-column-offset(0);
                }
            }
        }
    }
}
